object.property:
# Converts memory events into percentage with raw values
import time, re, logging

match = re.search('threshold of.* (swap|physicalmemory|virtualmemory).* (exceeded|restored|not met): current value ([\d\.]+)', evt.message, re.I)
if match and device:
    evt.component = match.groups()[0]
    if match.groups()[0].lower() in ["swap", "virtualmemory"]:
        total = device.os.totalSwap
        consumed = float(match.groups()[2]) #if counter MemoryComittedBytes
        if total:
            memoryavailable = total - consumed
        else:
            evt.summary = 'High Memory Utilization: Currently: %s used' %(convToUnits(consumed ))
            evt.message = evt.summary
    else:
        total = device.hw.totalMemory
        memoryavailable = float(match.groups()[2]) # if counter MemoryAvailableBytes
        if total:
            consumed = total - memoryavailable
        else:
            evt.summary = 'High Memory Utilization: Currently: %s free' %(convToUnits(memoryavailable))
            evt.message = evt.summary
    if total:
        percent_consumed = (consumed / total) * 100
        evt.summary = "High %s Utilization: Currently %3.0f%% used (%s free)" % (match.groups()[0], percent_consumed, convToUnits(memoryavailable))
        evt.message = evt.summary

if evt.eventClassKey == 'WindowsEventCollectionError' or  evt.eventKey == 'Windows Perfmon Corrupt Counters':
    if evt.severity > 2:
        evt.severity = 2

#drop all events for printers
ignore_pattern = ['Microsoft-Windows-PrintSpooler', 'Microsoft-Windows-TerminalServices-Printers','Microsoft-Windows-PrintSpooler', 'Print', 'DCOM', 'Microsoft-Windows-DistributedCOM', 'Schannel', 'BROWSER', 'Browser', 'Service Control Manager']

evt.severity = evt.severity-1
if evt.severity < 3:
    evt._action = "drop"
if evt.component is not None and evt.component in  ignore_pattern:
    evt._action = "drop"
if evt.component is not None and evt.component == 'TermServDevices' and evt.summary.find('Driver') != -1:
    evt._action = "drop"
if evt.summary.find('SYSTEM\CurrentControlSet\Services\SNMP\Parameters\TrapConfiguration') != -1:
    evt._action = "drop"
if evt.component is not None and evt.component == 'MRxSmb' and evt.summary.find('master browser') != -1:
    evt._action = "drop"
if evt.component is not None and evt.component == 'NetBT' and evt.summary.find('could not be registered on the Interface') != -1:
    evt._action = "drop"
if evt.component is not None and evt.component == 'Kerberos' and evt.summary.find('The kerberos client received a KRB_AP_ERR_MODIFIED error') != -1:
    evt._action = "drop"
if evt.component is not None and evt.component == 'Microsoft-Windows-GroupPolicy' and evt.eventClassKey == 'Microsoft-Windows-GroupPolicy_1006':
    evt._action = "drop"
if evt.component is not None and evt.eventClassKey in ['TermDD_56', 'TermDD_50']:
    if evt.severity > 2:
        evt.severity = 2
